    def __init__(self):
        self.technical_weights = {
            'wordplay': 0.25,
            'flow': 0.20,
            'technical_accuracy': 0.30,
            'structure': 0.25
        }

        self.impact_weights = {
            'insult_power': 0.30,
            'cleverness': 0.25,
            'authenticity': 0.20,
            'replay_value': 0.25
        }

    def analyze_technical_merit(self, lyrics: str) -> Dict[str, float]:
        scores = {
            'wordplay': self._score_wordplay(lyrics),
            'flow': self._score_flow(lyrics),
            'technical_accuracy': self._score_technical_accuracy(lyrics),
            'structure': self._score_structure(lyrics)
        }
        return scores

    def analyze_impact(self, lyrics: str) -> Dict[str, float]:
        scores = {
            'insult_power': self._score_insult_power(lyrics),
            'cleverness': self._score_cleverness(lyrics),
            'authenticity': self._score_authenticity(lyrics),
            'replay_value': self._score_replay_value(lyrics)
        }
        return scores

    def _score_wordplay(self, lyrics: str) -> float:
        # Count rhymes, alliteration, and word plays
        rhyme_patterns = self._find_rhyme_patterns(lyrics)
        alliteration_count = self._count_alliteration(lyrics)
        wordplay_count = self._count_wordplay(lyrics)

        # Normalize and weight components
        score = (rhyme_patterns * 0.4 + 
                alliteration_count * 0.3 + 
                wordplay_count * 0.3)
        return min(max(score, 0), 10)

    def _score_flow(self, lyrics: str) -> float:
        # Analyze rhythm and syllable patterns
        lines = lyrics.split('\n')
        rhythm_consistency = self._analyze_rhythm(lines)
        syllable_variation = self._analyze_syllables(lines)

        score = (rhythm_consistency * 0.6 + 
                syllable_variation * 0.4)
        return min(max(score, 0), 10)

    def _score_technical_accuracy(self, lyrics: str) -> float:
        # Check for industry terms and technical correctness
        industry_terms = self._count_industry_terms(lyrics)
        technical_accuracy = self._verify_technical_claims(lyrics)

        score = (industry_terms * 0.5 + 
                technical_accuracy * 0.5)
        return min(max(score, 0), 10)

    def _score_structure(self, lyrics: str) -> float:
        # Evaluate verse structure and organization
        verse_quality = self._analyze_verse_structure(lyrics)
        progression = self._analyze_progression(lyrics)

        score = (verse_quality * 0.5 + 
                progression * 0.5)
        return min(max(score, 0), 10)

    def _score_insult_power(self, lyrics: str) -> float:
        # Evaluate insult creativity and impact
        insult_count = self._count_insults(lyrics)
        insult_creativity = self._analyze_insult_creativity(lyrics)

        score = (insult_count * 0.4 + 
                insult_creativity * 0.6)
        return min(max(score, 0), 10)

    def _score_cleverness(self, lyrics: str) -> float:
        # Analyze clever references and wordplay
        references = self._count_clever_references(lyrics)
        wordplay = self._analyze_wordplay_complexity(lyrics)

        score = (references * 0.5 + 
                wordplay * 0.5)
        return min(max(score, 0), 10)

    def _score_authenticity(self, lyrics: str) -> float:
        # Check authenticity of technical claims
        technical_truth = self._verify_technical_authenticity(lyrics)
        industry_knowledge = self._analyze_industry_knowledge(lyrics)

        score = (technical_truth * 0.6 + 
                industry_knowledge * 0.4)
        return min(max(score, 0), 10)

    def _score_replay_value(self, lyrics: str) -> float:
        # Evaluate memorability and replayability
        memorable_lines = self._count_memorable_lines(lyrics)
        layered_meaning = self._analyze_meaning_layers(lyrics)

        score = (memorable_lines * 0.5 + 
                layered_meaning * 0.5)
        return min(max(score, 0), 10)

    def calculate_total_score(self, technical_scores: Dict[str, float], 
                            impact_scores: Dict[str, float]) -> float:
        technical_score = sum(score * self.technical_weights[metric]
                            for metric, score in technical_scores.items())

        impact_score = sum(score * self.impact_weights[metric]
                          for metric, score in impact_scores.items())

        # Combine scores (50-50 weight between technical and impact)
        total_score = (technical_score + impact_score) * 5  # Scale to 100
        return round(total_score, 1)

